#include <map>
#include <string>
#include <iostream>
#include <algorithm>
#include <vector>
#include <cmath>

using namespace std;

int main()
{
	map<char, int> anagram1;
	map<char, int> anagram2;

	map<char, int>::iterator map_itr;

	int numCharDelete = 0;

	string word1 = "";

	string word2 = "";


	// word 1
	for (size_t i = 0; i < word1.size(); i++)
	{
		if (anagram1.count(word1[i]) == 0)
		{
			anagram1[word1[i]] = 1;
		}
		else
		{
			anagram1[word1[i]] += 1;
		}
	}

	// word2
	for (size_t i = 0; i < word2.size(); i++)
	{
		if (anagram2.count(word2[i]) == 0)
		{
			anagram2[word2[i]] = 1;
		}
		else
		{
			anagram2[word2[i]] += 1;
		}
	}

	// count
	for (char ch = 'a'; ch <= 'z'; ++ch)
	{
		if (anagram1.count(ch) > 0 && anagram2.count(ch) > 0)
		{
			int a = anagram1[ch];
			int b = anagram2[ch];

			numCharDelete += abs(a - b);
		}
		else if (anagram1.count(ch) > 0)
		{
			numCharDelete += anagram1[ch];
		}
		else
		{
			numCharDelete += anagram2[ch];
		}
	}

	cout << "Number of char to be deleted: " << numCharDelete << endl;


	return 0;
}